// 목표! 최소값을 찾는다!
function getMin(a, b){
    if (a > b) {
        return b
    }
    return a // return을 만나면 나가기 때문에 else 문이 필요하지 않다.
}

function getMin2(a, b){
    var min;
    if (a > b) {
        min = b
    } else {
        min = a
    }
    return min
}

var min = getMin(3, 4)
console.log(min)

function getMinFromArray(numbers){
    var min = Infinity
    // TODO: 배열에서 최소값을 구하여 min에 할당
    for (num of numbers){
        min = min > num ? num : min //min = (min > num) ? num : min
        if (min > num){
            min = num
        }
    }
    return min
}

var numbers = [1, 2, 3, 4, 5]
var min = getMinFromArray(numbers)
console.log(min)

